// معالجة البيانات الواردة من النظام
const webhookData = $input.item.json;

// استخراج معلومات الطلب
const orderInfo = {
  order_id: webhookData.order_id || webhookData.body?.order_id,
  order_number: webhookData.order_number || webhookData.body?.order_number,
  old_status: webhookData.old_status || webhookData.body?.old_status,
  new_status: webhookData.new_status || webhookData.body?.new_status,
  customer_id: webhookData.customer_id || webhookData.body?.customer_id,
  customer_name: webhookData.customer_name || webhookData.body?.customer_name,
  timestamp: new Date().toISOString()
};

console.log('تم استقبال إشعار طلب جديد:', orderInfo);

return { json: orderInfo };

// يمكنك هنا إضافة أي عمليات خاصة بالطلبات المكتملة
// مثل: إرسال بريد إلكتروني، تحديث نظام خارجي، إلخ

const orderData = $input.item.json;

console.log('الطلب مكتمل - يمكن إضافة عمليات إضافية هنا');
console.log('رقم الطلب:', orderData.order_number);
console.log('اسم العميل:', orderData.customer_name);

return {
  json: {
    ...orderData,
    processed: true,
    action: 'طلب مكتمل - تم المعالجة'
  }
};

// معالجة الطلبات غير المكتملة
const orderData = $input.item.json;

console.log('تحديث حالة الطلب:', orderData.new_status);
console.log('رقم الطلب:', orderData.order_number);

return {
  json: {
    ...orderData,
    processed: true,
    action: 'تحديث حالة - تم المعالجة'
  }
};
